package main

import (
	"encoding/hex"
	"net"
	"fmt"
)

func WakeUp(macAddr, broadcastIP, bcastInterface string, port int) error {
	// Validate and parse the MAC address
	macBytes, err := hex.DecodeString(strings.Replace(macAddr, ":", "", -1))
	if err != nil {
		return fmt.Errorf("invalid MAC address: %v", err)
	}

	// Create the magic packet
	magicPacket := make([]byte, 6+16*len(macBytes))
	for i := 0; i < 6; i++ {
		magicPacket[i] = 0xFF
	}
	for i := 6; i < len(magicPacket); i += len(macBytes) {
		copy(magicPacket[i:], macBytes)
	}

	// Resolve the UDP address
	udpAddr, err := net.ResolveUDPAddr("udp", fmt.Sprintf("%s:%d", broadcastIP, port))
	if err != nil {
		return fmt.Errorf("failed to resolve UDP address: %v", err)
	}

	// Create the UDP connection
	conn, err := net.DialUDP("udp", nil, udpAddr)
	if err != nil {
		return fmt.Errorf("failed to dial UDP: %v", err)
	}
	defer conn.Close()

	// Set the network interface if specified
	if bcastInterface != "" {
		iface, err := net.InterfaceByName(bcastInterface)
		if err != nil {
			return fmt.Errorf("failed to find interface: %v", err)
		}
		conn.SetWriteDeadline(time.Now().Add(1 * time.Second))
		conn.WriteTo(magicPacket, &net.UDPAddr{
			IP:   net.ParseIP(broadcastIP),
			Port: port,
			Zone: iface.Name,
		})
	} else {
		// Send the magic packet
		_, err = conn.Write(magicPacket)
		if err != nil {
			return fmt.Errorf("failed to send magic packet: %v", err)
		}
	}

	return nil
}

func main() {
	// Example usage
	err := WakeUp("00:1A:2B:3C:4D:5E", "255.255.255.255", "", 9)
	if err != nil {
		fmt.Println("Error:", err)
	} else {
		fmt.Println("Magic packet sent successfully")
	}
}
