func remote2remoteUDP(remoteA, remoteB string, raenc, rbenc bool) {
	aAddr, err := net.ResolveUDPAddr("udp", remoteA)
	if err != nil {
		return
	}
	bAddr, err := net.ResolveUDPAddr("udp", remoteB)
	if err != nil {
		return
	}

	connA, err := net.DialUDP("udp", nil, aAddr)
	if err != nil {
		return
	}
	defer connA.Close()

	connB, err := net.DialUDP("udp", nil, bAddr)
	if err != nil {
		return
	}
	defer connB.Close()

	var wg sync.WaitGroup
	wg.Add(2)

	go func() {
		defer wg.Done()
		forward(connA, connB, raenc)
	}()

	go func() {
		defer wg.Done()
		forward(connB, connA, rbenc)
	}()

	wg.Wait()
}

func forward(src, dst *net.UDPConn, encrypt bool) {
	buffer := make([]byte, 4096)
	for {
		n, err := src.Read(buffer)
		if err != nil {
			return
		}

		if encrypt {
			buffer = encryptPacket(buffer[:n])
		}

		_, err = dst.Write(buffer[:n])
		if err != nil {
			return
		}
	}
}

func encryptPacket(packet []byte) []byte {
	key := []byte("your-256-bit-key") // Replace with your actual key
	block, err := aes.NewCipher(key)
	if err != nil {
		panic(err) // Handle error properly in production
	}

	nonce := make([]byte, 12) // Use a proper nonce in production
	aesgcm, err := cipher.NewGCM(block)
	if err != nil {
		panic(err) // Handle error properly in production
	}

	encrypted := aesgcm.Seal(nil, nonce, packet, nil)
	return encrypted
