package main

import (
	"context"
	"net"
	"sync"
	"sync/atomic"
	"time"
	"log"
	"encoding/binary"
	"errors"
)

type Bootstrap struct {
	fqdn        string
	port        uint16
	bootstrap   *net.UDPAddr
	qt          uint16
	logger      *log.Logger
	updating    atomic.Bool
	nextUpdate  time.Time
	readyNotify chan struct{}
	m           sync.Mutex
	ready       bool
	addrStr     string
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	b.m.Lock()
	if b.ready && b.qt == qt {
		b.m.Unlock()
		return netip.ParseAddr(b.addrStr)
	}
	b.m.Unlock()

	if !b.updating.CompareAndSwap(false, true) {
		select {
		case <-b.readyNotify:
		case <-ctx.Done():
			return netip.Addr{}, 0, ctx.Err()
		}
		b.m.Lock()
		defer b.m.Unlock()
		if b.ready && b.qt == qt {
			return netip.ParseAddr(b.addrStr)
		}
		return netip.Addr{}, 0, errors.New("resolution failed")
	}
	defer b.updating.Store(false)

	conn, err := net.DialUDP("udp", nil, b.bootstrap)
	if err != nil {
		return netip.Addr{}, 0, err
	}
	defer conn.Close()

	msg := make([]byte, 512)
	msg[0] = 1 // Transaction ID
	msg[1] = 0 // Transaction ID
	msg[2] = 1 // Flags
	msg[3] = 0 // Flags
	msg[4] = 0 // Questions
	msg[5] = 1 // Questions
	copy(msg[12:], b.fqdn)
	msg[12+len(b.fqdn)+1] = 0 // Null-terminate FQDN
	binary.BigEndian.PutUint16(msg[12+len(b.fqdn)+2:], qt)
	binary.BigEndian.PutUint16(msg[12+len(b.fqdn)+4:], 1) // Class IN

	_, err = conn.Write(msg)
	if err != nil {
		return netip.Addr{}, 0, err
	}

	err = conn.SetReadDeadline(time.Now().Add(5 * time.Second))
	if err != nil {
		return netip.Addr{}, 0, err
	}

	n, _, err := conn.ReadFromUDP(msg)
	if err != nil {
		return netip.Addr{}, 0, err
	}

	// Parse response (simplified)
	if n < 12 {
		return netip.Addr{}, 0, errors.New("invalid DNS response")
	}
	answers := binary.BigEndian.Uint16(msg[6:8])
	if answers == 0 {
		return netip.Addr{}, 0, errors.New("no answers in DNS response")
	}

	offset := 12 + len(b.fqdn) + 5
	for i := 0; i < int(answers); i++ {
		offset += 12 // Skip name, type, class
		ttl := binary.BigEndian.Uint32(msg[offset : offset+4])
		rdlength := binary.BigEndian.Uint16(msg[offset+4 : offset+6])
		offset += 6
		if rdlength == 4 { // IPv4
			ip := netip.AddrFrom4([4]byte(msg[offset : offset+4]))
			b.m.Lock()
			b.ready = true
			b.qt = qt
			b.addrStr = ip.String()
			b.nextUpdate = time.Now().Add(time.Duration(ttl) * time.Second)
			close(b.readyNotify)
			b.readyNotify = make(chan struct{})
			b.m.Unlock()
			return ip, ttl, nil
		}
		offset += int(rdlength)
	}

	return netip.Addr{}, 0, errors.New("no valid IP address found in DNS response")
}

func main() {
	// Example usage
	logger := log.New(os.Stdout, "dns-resolver: ", log.LstdFlags)
	bootstrap := &Bootstrap{
		fqdn:      "example.com",
		port:      53,
		bootstrap: &net.UDPAddr{IP: net.ParseIP("8.8.8.8"), Port: 53},
		qt:        1, // A record
		logger:    logger,
		readyNotify: make(chan struct{}),
	}
	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()
	addr, ttl, err := bootstrap.resolve(ctx, 1)
	if err != nil {
		logger.Println("Error resolving:", err)
	} else {
		logger.Printf("Resolved address: %s, TTL: %d\n", addr, ttl)
	}
}
