package main

import (
	"fmt"
	"net/http"
	"net/url"
	"sync"

	"github.com/gorilla/websocket"
)

type WebsocketProxy struct {
	Director func(*http.Request, http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (wp *WebsocketProxy) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	backendURL := wp.Backend(r)
	if backendURL == nil {
		http.Error(w, "Invalid backend URL", http.StatusInternalServerError)
		return
	}

	backendHeader := http.Header{}
	if wp.Director != nil {
		wp.Director(r, backendHeader)
	}

	backendConn, _, err := wp.Dialer.Dial(backendURL.String(), backendHeader)
	if err != nil {
		http.Error(w, fmt.Sprintf("Failed to connect to backend: %v", err), http.StatusBadGateway)
		return
	}
	defer backendConn.Close()

	clientConn, err := wp.Upgrader.Upgrade(w, r, nil)
	if err != nil {
		http.Error(w, fmt.Sprintf("Failed to upgrade client connection: %v", err), http.StatusBadRequest)
		return
	}
	defer clientConn.Close()

	errChan := make(chan error, 2)
	var wg sync.WaitGroup
	wg.Add(2)

	go wp.replicateWebsocketConn(clientConn, backendConn, errChan)
	go wp.replicateWebsocketConn(backendConn, clientConn, errChan)

	wg.Wait()
	close(errChan)
	for err := range errChan {
		if err != nil {
			fmt.Printf("Connection error: %v\n", err)
		}
	}
}

func (wp *WebsocketProxy) replicateWebsocketConn(src, dst *websocket.Conn, errChan chan error) {
	defer func() {
		errChan <- src.Close()
		errChan <- dst.Close()
	}()

	for {
		msgType, msg, err := src.ReadMessage()
		if err != nil {
			errChan <- err
			return
		}

		err = dst.WriteMessage(msgType, msg)
		if err != nil {
			errChan <- err
			return
		}
	}
}

func main() {
	// Example usage
	upgrader := &websocket.Upgrader{
		ReadBufferSize:  1024,
		WriteBufferSize: 1024,
	}

	dialer := &websocket.Dialer{
		HandshakeTimeout: 45 * time.Second,
	}

	proxy := &WebsocketProxy{
		Director: func(r *http.Request, h http.Header) {
			h.Set("X-Forwarded-For", r.RemoteAddr)
		},
		Backend: func(r *http.Request) *url.URL {
			return &url.URL{Scheme: "ws", Host: "localhost:8081", Path: "/ws"}
		},
		Upgrader: upgrader,
		Dialer:   dialer,
	}

