func readFileInUserNamespace(nsPath, path string) ([]byte, error) {
	// Create pipes for stdout and stderr
	stdoutR, stdoutW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stdoutR.Close()
	defer stdoutW.Close()

	stderrR, stderrW, err := os.Pipe()
	if err != nil {
		return nil, err
	}
	defer stderrR.Close()
	defer stderrW.Close()

	// Command to run in the new user namespace
	cmd := exec.Command("unshare", "--user", "--map-root-user", "--", "cat", path)
	cmd.Stdout = stdoutW
	cmd.Stderr = stderrW
	cmd.SysProcAttr = &syscall.SysProcAttr{
		Cloneflags: syscall.CLONE_NEWUSER,
	}

	// Set the user namespace
	if err := os.WriteFile(nsPath, []byte("0\n"), 0644); err != nil {
		return nil, err
	}

	// Start the command
	if err := cmd.Start(); err != nil {
		return nil, err
	}
	defer cmd.Wait()

	// Close the write ends of the pipes
	stdoutW.Close()
	stderrW.Close()

	// Read stdout
	var out bytes.Buffer
	if _, err := io.Copy(&out, stdoutR); err != nil {
		return nil, err
	}

	// Read stderr
	var errOut bytes.Buffer
	if _, err := io.Copy(&errOut, stderrR); err != nil {
		return nil, err
	}

	// Check if there was an error output
	if errOut.Len() > 0 {
		return nil, fmt.Errorf("error reading file: %s", errOut.String())
	}

	return out.Bytes(), nil
}

func main() {
	// Example usage
	nsPath := "/proc/self/uid_map"
	filePath := "/path/to/file"
	contents, err := readFileInUserNamespace(nsPath, filePath)
	if err != nil {
		fmt.Println("Error:", err)
	} else {
		fmt.Println("File contents:", string(contents))
	}
}
