package main

import (
	"io"
	"os"
)

type fileCloner struct {
	file *os.File
}

func (fc *fileCloner) Write(p []byte) (int, error) {
	return fc.file.Write(p)
}

type logRetriever struct {
	reader *os.File
	writer *os.File
	output *fileCloner
}

func newlogRetriever(logFile string) (*logRetriever, error) {
	r, w, err := os.Pipe()
	if err != nil {
		return nil, err
	}

	f, err := os.OpenFile(logFile, os.O_CREATE|os.O_WRONLY|os.O_APPEND, 0666)
	if err != nil {
		r.Close()
		w.Close()
		return nil, err
	}

	cloner := &fileCloner{file: f}
	retriever := &logRetriever{
		reader: r,
		writer: w,
		output: cloner,
	}

	go func() {
		defer w.Close()
		io.Copy(w, f)
	}()

	return retriever, nil
}

func (lr *logRetriever) Read(p []byte) (int, error) {
	return lr.reader.Read(p)
}

func (lr *logRetriever) CloseWriter() error {
	return lr.writer.Close()
}

func (lr *logRetriever) Close() error {
	err1 := lr.reader.Close()
	err2 := lr.output.file.Close()
	if err1 != nil {
		return err1
	}
	return err2
}

func (lr *logRetriever) Opt() cio.Opt {
	return cio.Opt{
		Reader: lr.reader,
		Writer: lr.output,
	}
}
