var responses = make(map[string]string)

func rootHandler(w http.ResponseWriter, r *http.Request) {
	proxifyKey := r.Header.Get("proxify")
	if response, ok := responses[proxifyKey]; ok {
		w.Write([]byte(response))
		delete(responses, proxifyKey)
	} else {
		http.Error(w, "No response found for proxify key", http.StatusNotFound)
	}
}

func main() {
	http.HandleFunc("/", rootHandler)
	log.Fatal(http.ListenAndServe(options.HTTPListenerAddress, nil))
