type options struct {
	LogLevel   string
	CommitDepth int
}

func serveCmd(o *options) *cobra.Command {
	cmd := &cobra.Command{
		Use:   "serve",
		Short: "Starts the HTTP server for scorecard results",
		Run: func(cmd *cobra.Command, args []string) {
			http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
				repoURL := r.URL.Query().Get("repo")
				if repoURL == "" {
					http.Error(w, "Missing 'repo' query parameter", http.StatusBadRequest)
					return
				}

				results, err := scorecard.RunChecks(repoURL, o.CommitDepth)
				if err != nil {
					http.Error(w, err.Error(), http.StatusInternalServerError)
					return
				}

				accept := r.Header.Get("Accept")
				if accept == "application/json" {
					w.Header().Set("Content-Type", "application/json")
					json.NewEncoder(w).Encode(results)
				} else {
					tmpl, err := template.ParseFiles("template.html")
					if err != nil {
						log.Panicf("Failed to parse template: %v", err)
					}
					tmpl.Execute(w, results)
				}
			})

			log.Printf("Server starting on port 8080...")
			if err := http.ListenAndServe(":8080", nil); err != nil {
				log.Panicf("Failed to start server: %v", err)
			}
		},
	}

	cmd.Flags().StringVarP(&o.LogLevel, "log-level", "l", "info", "Set the logging level")
	cmd.Flags().IntVarP(&o.CommitDepth, "commit-depth", "d", 10, "Set the commit depth for analysis")

	return cmd
}

func main() {
	var o options
	cmd := serveCmd(&o)
	if err := cmd.Execute(); err != nil {
		log.Fatal(err)
	}
}
