package main

import (
	"net/http"
	"time"
	"github.com/prometheus/client_golang/prometheus"
	"github.com/prometheus/client_golang/prometheus/promhttp"
)

type Collector struct {
	PacketCount int
}

func (c *Collector) Describe(ch chan<- *prometheus.Desc) {
	ch <- prometheus.NewDesc("packet_count", "Number of packets received", nil, nil)
}

func (c *Collector) Collect(ch chan<- prometheus.Metric) {
	ch <- prometheus.MustNewConstMetric(
		prometheus.NewDesc("packet_count", "Number of packets received", nil, nil),
		prometheus.CounterValue,
		float64(c.PacketCount),
	)
}

var (
	uptime = prometheus.NewCounter(prometheus.CounterOpts{
		Name: "uptime_seconds",
		Help: "Number of seconds the server has been up",
	})
)

func init() {
	prometheus.MustRegister(uptime)
}

func ServeMetricsAt(addr string, c *Collector) {
	if c != nil {
		prometheus.MustRegister(c)
	}

	http.HandleFunc("/metrics", func(w http.ResponseWriter, r *http.Request) {
		uptime.Inc()
		if c != nil {
			c.PacketCount++
		}
		promhttp.Handler().ServeHTTP(w, r)
	})

	go func() {
		http.ListenAndServe(addr, nil)
	}()
}

func main() {
	c := &Collector{}
	ServeMetricsAt(":8080", c)
	select {}
}
