package main

import (
	"net/http"
	"github.com/golang/glog"
	"golang.org/x/net/webdav"
)

type CustomHandler struct {
	FileSystem webdav.FileSystem
	LockSystem webdav.LockSystem
	Logger     func(*http.Request, error)
}

func (h *CustomHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	if r.Header.Get("Special-Header") == "SpecialValue" {
		w.WriteHeader(http.StatusForbidden)
		return
	}
	h.Logger(r, nil)
	webdav.Handler{
		FileSystem: h.FileSystem,
		LockSystem: h.LockSystem,
	}.ServeHTTP(w, r)
}

func NewMemFS() webdav.FileSystem {
	return webdav.NewMemFS()
}

func NewMemLS() webdav.LockSystem {
	return webdav.NewMemLS()
}

func requestHandler(handler http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		glog.Infof("Request received: %s %s", r.Method, r.URL.Path)
		handler.ServeHTTP(w, r)
	})
}

func main() {
	fs := NewMemFS()
	ls := NewMemLS()

	handler := &CustomHandler{
		FileSystem: fs,
		LockSystem: ls,
		Logger: func(r *http.Request, err error) {
			if err != nil {
				glog.Errorf("Error handling request: %v", err)
			} else {
				glog.Infof("Processed request: %s %s", r.Method, r.URL.Path)
			}
		},
	}

	http.Handle("/webdav/", requestHandler(handler))
	glog.Info("Starting WebDAV server on :8080")
	glog.Flush()
	http.ListenAndServe(":8080", nil)
}

