package main

import (
	"flag"
	"fmt"
	"log"
	"net/http"
	"os"

	"golang.org/x/net/webdav"
)

type MemFS struct {
	data map[string][]byte
}

func (fs *MemFS) Mkdir(name string, perm os.FileMode) error {
	if _, ok := fs.data[name]; ok {
		return os.ErrExist
	}
	fs.data[name] = nil
	return nil
}

func (fs *MemFS) OpenFile(name string, flag int, perm os.FileMode) (webdav.File, error) {
	data, ok := fs.data[name]
	if !ok && flag&os.O_CREATE == 0 {
		return nil, os.ErrNotExist
	}
	if flag&os.O_TRUNC != 0 {
		data = nil
	}
	return &MemFile{name: name, data: data, fs: fs}, nil
}

func (fs *MemFS) RemoveAll(name string) error {
	delete(fs.data, name)
	return nil
}

func (fs *MemFS) Rename(oldName, newName string) error {
	data, ok := fs.data[oldName]
	if !ok {
		return os.ErrNotExist
	}
	fs.data[newName] = data
	delete(fs.data, oldName)
	return nil
}

func (fs *MemFS) Stat(name string) (os.FileInfo, error) {
	data, ok := fs.data[name]
	if !ok {
		return nil, os.ErrNotExist
	}
	return &MemFileInfo{name: name, size: int64(len(data))}, nil
}

type MemFile struct {
	name string
	data []byte
	fs   *MemFS
	offset int64
}

func (f *MemFile) Close() error {
	f.fs.data[f.name] = f.data
	return nil
}

func (f *MemFile) Read(p []byte) (int, error) {
	if f.offset >= int64(len(f.data)) {
		return 0, io.EOF
	}
	n := copy(p, f.data[f.offset:])
	f.offset += int64(n)
	return n, nil
}

func (f *MemFile) Seek(offset int64, whence int) (int64, error) {
	switch whence {
	case os.SEEK_SET:
		f.offset = offset
	case os.SEEK_CUR:
		f.offset += offset
	case os.SEEK_END:
		f.offset = int64(len(f.data)) + offset
	default:
		return 0, fmt.Errorf("invalid whence")
	}
	return f.offset, nil
}

func (f *MemFile) Write(p []byte) (int, error) {
	newOffset := f.offset + int64(len(p))
	if newOffset > int64(cap(f.data)) {
		newData := make([]byte, newOffset)
		copy(newData, f.data)
		f.data = newData
	}
	copy(f.data[f.offset:], p)
	f.offset = newOffset
	return len(p), nil
}

func (f *MemFile) Stat() (os.FileInfo, error) {
	return &MemFileInfo{name: f.name, size: int64(len(f.data))}, nil
}

type MemFileInfo struct {
	name string
	size int64
}

func (fi *MemFileInfo) Name() string       { return fi.name }
func (fi *MemFileInfo) Size() int64        { return fi.size }
func (fi *MemFileInfo) Mode() os.FileMode  { return 0644 }
func (fi *MemFileInfo) ModTime() time.Time { return time.Now() }
func (fi *MemFileInfo) IsDir() bool        { return fi.size == 0 }
func (fi *MemFileInfo) Sys() interface{}   { return nil }

type MemLS struct {
	locks map[string]*webdav.LockDetails
}

func (ls *MemLS) Create.LockDetails) error {
	if _, ok := ls.locks[details.Path]; ok {
		return webdav.ErrLocked
	}
	ls.locks[details.Path] = details
	return nil
}

func (ls *MemLS) Refresh(lock *webdav.LockDetails) error {
	if existing, ok := ls.locks[lock.Path]; ok {
		existing.Timeout = lock.Timeout
		return nil
	}
	return webdav.ErrNoSuchLock
}

func (ls *MemLS) Unlock(lock *webdav.LockDetails) error {
	if existing, ok := ls.locks[lock.Path]; ok {
		delete(ls.locks, lock.Path)
		return nil
	}
	return webdav.ErrNoSuchLock
}

func customLogger(r *http.Request, err error) {
	if err != nil {
		log.Printf("WebDAV error: %v, Request: %s %s", err, r.Method, r.URL.Path)
	} else {
		log.Printf("WebDAV request: %s %s", r.Method, r.URL.Path)
	}
}

func main() {
	port := flag.String("port", "8080", "Port to listen on")
	flag.Parse()

	fs := &MemFS{data: make(map[string][]byte)}
	ls := &MemLS{locks: make(map[string]*webdav.LockDetails)}
	handler := &webdav.Handler{
		FileSystem: fs,
		LockSystem: ls,
		Logger:     customLogger,
	}

	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		if r.URL.Path == "/litmus/props/3" {
			http.Error(w, "Special handling for litmus props: 3", http.StatusForbidden)
			return
		}
		handler.ServeHTTP(w, r)
	})

	log.Fatal(http.ListenAndServe(":"+*port, nil))
}
