package main

import (
	"bytes"
	"crypto/tls"
	"errors"
	"io/ioutil"
	"net/http"
)

type EtcdRequestOption struct {
	Endpoint  string
	Api       string
	PostData  string
	TlsConfig *tls.Config
	Method    string
	Silent    bool
}

func DoRequest(opt EtcdRequestOption) (string, error) {
	client := &http.Client{}
	if opt.TlsConfig != nil {
		client.Transport = &http.Transport{
			TLSClientConfig: opt.TlsConfig,
		}
	}

	url := opt.Endpoint + opt.Api
	var req *http.Request
	var err error

	if opt.Method == "POST" && opt.PostData != "" {
		req, err = http.NewRequest(opt.Method, url, bytes.NewBuffer([]byte(opt.PostData)))
	} else {
		req, err = http.NewRequest(opt.Method, url, nil)
	}
	if err != nil {
		return "", err
	}

	resp, err := client.Do(req)
	if err != nil {
		return "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return "", errors.New("HTTP request failed with status: " + resp.Status)
	}

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return "", err
	}

	return string(body), nil
}

func main() {
	// Example usage
	opt := EtcdRequestOption{
		Endpoint: "https://example.com",
		Api:      "/api/v1/data",
		PostData: `{"key": "value"}`,
		TlsConfig: &tls.Config{
			InsecureSkipVerify: true,
		},
		Method: "POST",
	}
	response, err := DoRequest(opt)
	if err != nil {
		panic(err)
	}
	println(response)
}
