package main

import (
	"context"
	"crypto/tls"
	"fmt"
	"net/http"
	"time"

	"github.com/labstack/echo/v4"
)

type Api struct {
	port        int
	bindIp      string
	tlsCertFile string
	tlsKeyFile  string
	timeout     time.Duration
	rootPath    string
	srv         *echo.Echo
}

func (a *Api) Start() error {
	a.srv = echo.New()
	a.srv.HideBanner = true
	a.srv.HidePort = true

	a.srv.GET(a.rootPath, func(c echo.Context) error {
		return c.String(http.StatusOK, "Server is running")
	})

	server := &http.Server{
		Addr:         fmt.Sprintf("%s:%d", a.bindIp, a.port),
		Handler:      a.srv,
		ReadTimeout:  a.timeout,
		WriteTimeout: a.timeout,
	}

	if a.tlsCertFile != "" && a.tlsKeyFile != "" {
		tlsConfig := &tls.Config{
			MinVersion: tls.VersionTLS12,
		}
		server.TLSConfig = tlsConfig
		go func() {
			if err := a.srv.StartTLS(server.Addr, a.tlsCertFile, a.tlsKeyFile); err != nil && err != http.ErrServerClosed {
				a.srv.Logger.Fatal(err)
			}
		}()
	} else {
		go func() {
			if err := a.srv.Start(server.Addr); err != nil && err != http.ErrServerClosed {
				a.srv.Logger.Fatal(err)
			}
		}()
	}

	return nil
}

func (a *Api) Stop(ctx context.Context) error {
	return a.srv.Shutdown(ctx)
}

func main() {
	api := &Api{
		port:        8080,
		bindIp:      "0.0.0.0",
		tlsCertFile: "cert.pem",
		tlsKeyFile:  "key.pem",
		timeout:     10 * time.Second,
		rootPath:    "/",
	}

	if err := api.Start(); err != nil {
		fmt.Println("Failed to start server:", err)
		return
	}

	fmt.Println("Server started on port", api.port)

	// Simulate server running
	time.Sleep(1 * time.Hour)

	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()

	if err := api.Stop(ctx); err != nil {
		fmt.Println("Failed to stop server:", err)
		return
	}

	fmt.Println("Server stopped gracefully")
}
