	bucket    int
	mu        sync.Mutex
	tokens    int
	lastCheck time.Time
}

func NewRateLimiter(rps int) *RateLimiter {
	return &RateLimiter{
		rps:       rps,
		bucket:    rps,
		tokens:    rps,
		lastCheck: time.Now(),
	}
}

func (rl *RateLimiter) Allow() bool {
	rl.mu.Lock()
	defer rl.mu.Unlock()

	now := time.Now()
	elapsed := now.Sub(rl.lastCheck).Seconds()
	rl.lastCheck = now
	rl.tokens += int(elapsed * float64(rl.rps))
	if rl.tokens > rl.bucket {
		rl.tokens = rl.bucket
	}

	if rl.tokens > 0 {
		rl.tokens--
		return true
	}
	return false
}

func RateLimitMiddleware(rl *RateLimiter) gin.HandlerFunc {
	return func(c *gin.Context) {
		if !rl.Allow() {
			c.JSON(http.StatusTooManyRequests, gin.H{"error": "rate limit exceeded"})
			c.Abort()
			return
		}
		c.Next()
	}
}

