func (r *MetaDataRequest) SubResource(subResource string) IMetaDataRequest {
    r.subResource = subResource
    return r
}

func (r *MetaDataRequest) Url() (string, error) {
    if r.resource == "" {
        return "", fmt.Errorf("resource is required")
    }
    url := fmt.Sprintf("http://metadata.service/%s/%s/%s", r.version, r.resourceType, r.resource)
    if r.subResource != "" {
        url += "/" + r.subResource
    }
    return url, nil
}

func (r *MetaDataRequest) Do(response interface{}) error {
    url, err := r.Url()
    if err != nil {
        return err
    }
    resp, err := r.send()
    if err != nil {
        return err
    }
    return r.Decode(resp, response)
}

func (r *MetaDataRequest) send() (string, error) {
    req, err := http.NewRequest("GET", r.Url(), nil)
    if err != nil {
        return "", err
    }
    if r.sendRequest != nil {
        return r.sendRequest(req)
    }
    resp, err := r.client.Do(req)
    if err != nil {
        return "", err
    }
    defer resp.Body.Close()
    body, err := io.ReadAll(resp.Body)
    if err != nil {
        return "", err
    }
    return string(body), nil
}

func (r *MetaDataRequest) Decode(body string, response interface{}) error {
    return json.Unmarshal([]byte(body), response)
}

