	u, err := url.Parse(m.url)
	if err != nil {
		return nil, err
	}

	q := u.Query()
	q.Set("community", community)
	q.Set("password", password)
	u.RawQuery = q.Encode()

	req, err := http.NewRequest(http.MethodPost, u.String(), http.NoBody)
	if err != nil {
		return nil, err
	}
	req.SetBasicAuth(m.username, m.password)

