	if err != nil {
		return nil, err
	}
	if res.Body != nil {
		defer res.Body.Close()
	}
	if res.StatusCode != http.StatusOK {
		return nil, errors.New(res.Status)
	}

	body, err := ioutil.ReadAll(res.Body)
	if err != nil {
		return nil, err
	}

	c := persisters.Community{}
	if err := json.Unmarshal(body, &c); err != nil {
		return nil, err
	}

	return &c, nil
}

