		xlog.Jupiter().Panic("start producer",
			xlog.FieldName(pc.name),
			xlog.FieldExtMessage(pc.ProducerConfig),
			xlog.Any("error", err),
		)
	}

	pc.started = true
	pc.Producer = client
	return nil
}

func (pc *Producer) Close() error {
	err := pc.Shutdown()
	if err != nil {
		xlog.Jupiter().Warn("consumer close fail", xlog.Any("error", err.Error()))
		return err
	}
	return nil
}

