func (u *UploadSession) uploadChunk(auth *graph.Auth, offset uint64) ([]byte, int, error) {
    u.Lock()
    defer u.Unlock()

    chunkSize := 1024 * 1024 // 1 MB
    end := offset + uint64(chunkSize)
    if end > u.Size {
        end = u.Size
    }

    req, err := http.NewRequest("PUT", u.UploadURL, bytes.NewReader(u.Data[offset:end]))
    if err != nil {
        return nil, 0, err
    }

    req.Header.Set("Content-Range", fmt.Sprintf("bytes %d-%d/%d", offset, end-1, u.Size))
    req.Header.Set("Authorization", "Bearer "+auth.Token)

    resp, err := http.DefaultClient.Do(req)
    if err != nil {
        return nil, 0, err
    }
    defer resp.Body.Close()

    if resp.StatusCode == http.StatusOK || resp.StatusCode == http.StatusAccepted {
        body, _ := io.ReadAll(resp.Body)
        u.ETag = resp.Header.Get("ETag")
        return body, resp.StatusCode, nil
    }

