		res.Provider = psp
		res.Content = notif
		res.Err = push.NewErrorf("Failed to decode %s response: %v", psb.initialism, err)
		resQueue <- res
		return
	}

	psb.handleCMMulticastResults(psp, dpList, resQueue, notif, result.Results)
}

