	binary.BigEndian.PutUint32(buf[16:], info.GetRectWidth())
	binary.BigEndian.PutUint32(buf[20:], info.GetRectHeight())
	binary.BigEndian.PutUint32(buf[24:], uint32(srcSize))
	copy(buf[28:], data)
	conn.WriteMessage(websocket.BinaryMessage, buf)
}

