import (
	"context"
	"crypto/tls"
	"errors"
	"fmt"
	"io"
	"net"
	"net/http"
	"time"

	"github.com/gorilla/websocket"
)

type Conn struct {
	wsConn *websocket.Conn
	reader io.Reader
}

func New(wsConn *websocket.Conn) *Conn {
	return &Conn{
		wsConn: wsConn,
		reader: nil,
	}
}

func Dial(ctx context.Context, url string, opts ...DialOption) (*Conn, error) {
	options := dialOptions{}
	for _, o := range opts {
		o.apply(&options)
	}

	dialer := &websocket.Dialer{
		HandshakeTimeout: 60 * time.Second,
	}

	if options.tlsConfig != nil {
		dialer.TLSClientConfig = options.tlsConfig
	}

	header := make(http.Header)
	if options.token != "" {
		header.Set("Authorization", "Bearer "+options.token)
	}

	wsConn, resp, err := dialer.DialContext(
		ctx, url, header,
	)
	if err == nil {
		return New(wsConn), nil
	}
	if resp == nil {
		return nil, NewRetryableError(err)
	}
	defer resp.Body.Close()

	err = fmt.Errorf("%d: %w", resp.StatusCode, err)
	if _, ok := retryableStatusCodes[resp.StatusCode]; ok {
		return nil, NewRetryableError(err)
	}
	return nil, err
}

func (c *Conn) Read(b []byte) (int, error) {
	for {
		if c.reader == nil {
			mt, r, err := c.wsConn.NextReader()
			if err != nil {
				var closeErr *websocket.CloseError
				if errors.As(err, &closeErr) {
					return 0, net.ErrClosed
				}
				return 0, err
			}
			if mt != websocket.BinaryMessage {
				return 0, fmt.Errorf("unexpected message type: %d", mt)
			}
			c.reader = r
		}

		n, err := c.reader.Read(b)
		if n > 0 {
			if err != nil {
				c.reader = nil
				if err == io.EOF {
					err = nil
				}
			}
			return n, err
		}
		if err != io.EOF {
			var closeErr *websocket.CloseError
			if errors.As(err, &closeErr) {
				return 0, net.ErrClosed
			}
			return 0, err
		}

		c.reader = nil
	}
}

func (c *Conn) Write(b []byte) (int, error) {
	if err := c.wsConn.WriteMessage(websocket.BinaryMessage, b); err != nil {
		var closeErr *websocket.CloseError
		if errors.As(err, &closeErr) {
			return 0, net.ErrClosed
		}
		return 0, err
	}
	return len(b), nil
}

func (c *Conn) Close() error {
	return c.wsConn.Close()
}

func (c *Conn) LocalAddr() net.Addr {
	return c.wsConn.LocalAddr()
}

func (c *Conn) RemoteAddr() net.Addr {
	return c.wsConn.RemoteAddr()
}

func (c *Conn) SetDeadline(t time.Time) error {
	if err := c.SetReadDeadline(t); err != nil {
		return err
	}
	return c.SetWriteDeadline(t)
}

func (c *Conn) SetReadDeadline(t time.Time) error {
	return c.wsConn.SetReadDeadline(t)
}

func (c *Conn) SetWriteDeadline(t time.Time) error {
	return c.wsConn.SetWriteDeadline(t)
}

