    client := &http.Client{
        Timeout: job.Timeout,
    }

    jsonPayload, err := json.Marshal(job.Payload)
    if err != nil {
        h.Logger.Error().Err(err).Msg("Failed to marshal job payload")
        return &jobqueue.Result{Status: jobqueue.Failed, Code: 500, Message: "Payload marshal error"}
    }

    req, err := http.NewRequest("POST", job.URL, bytes.NewBuffer(jsonPayload))
    if err != nil {
        h.Logger.Error().Err(err).Msg("Failed to create HTTP request")
        return &jobqueue.Result{Status: jobqueue.Failed, Code: 500, Message: "Request creation error"}
    }

    req.Header.Set("User-Agent", h.UserAgent)
    req.Header.Set("Content-Type", "application/json")

    resp, err := client.Do(req)
    if err != nil {
        h.Logger.Error().Err(err).Msg("HTTP request execution failed")
        return &jobqueue.Result{Status: jobqueue.Failed, Code: 500, Message: "Request execution error"}
    }
    defer resp.Body.Close()

    responseBody, err := ioutil.ReadAll(resp.Body)
    if err != nil {
        h.Logger.Error().Err(err).Msg("Failed to read response body")
        return &jobqueue.Result{Status: jobqueue.Failed, Code: 500, Message: "Response reading error"}
    }

    if resp.StatusCode != http.StatusOK {
        h.Logger.Error().Msgf("Received non-200 response: %s", responseBody)
        return &jobqueue.Result{Status: jobqueue.Failed, Code: resp.StatusCode, Message: string(responseBody)}
    }

    h.Logger.Info().Msg("Job processed successfully")
    return &jobqueue.Result{Status: jobqueue.Success, Code: 200, Message: string(responseBody)}
}

