func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	body, err := ioutil.ReadAll(c.Request().Body)
	if err != nil {
		h.log.Error(err)
		return c.String(http.StatusInternalServerError, "Error reading request body")
	}
	h.majsoulMessageQueue <- body
	return c.String(http.StatusOK, "Majsoul message queued")
}

