package main

import (
	"fmt"
	"io/ioutil"
	"net/http"
	"time"
)

const metadataBaseURL = "http://metadata.google.internal/computeMetadata/v1/"

func getETag(suffix string) (value string, etag string, err error) {
	client := &http.Client{
		Timeout: 5 * time.Second,
	}

	req, err := http.NewRequest("GET", metadataBaseURL+suffix, nil)
	if err != nil {
		return "", "", err
	}
	req.Header.Set("Metadata-Flavor", "Google")

	resp, err := client.Do(req)
	if err != nil {
		return "", "", err
	}
	defer resp.Body.Close()

	if resp.StatusCode == http.StatusOK {
		body, err := ioutil.ReadAll(resp.Body)
		if err != nil {
			return "", "", err
		}
		return string(body), resp.Header.Get("ETag"), nil
	} else if resp.StatusCode == http.StatusNotFound {
		return "", "", fmt.Errorf("metadata not found")
	}
	return "", "", fmt.Errorf("unexpected status code: %d", resp.StatusCode)
}

