package main

import (
	"github.com/gofiber/fiber/v2"
	"github.com/go-sentinel/sentinel-go"
	"github.com/go-sentinel/sentinel-go/transport/http"
	"net/http"
)

func main() {
	app := fiber.New()

	sentinel := sentinel.New()
	sentinel.SetTransport(http.NewTransport())

	app.Use(func(c *fiber.Ctx) error {
		ip := c.Get("X-Real-IP")
		if ip == "" {
			ip = c.IP()
		}

		resource := "rate_limit:" + ip
		blocked := sentinel.Entry(resource, sentinel.WithResource(resource))

		if blocked != nil {
			return c.Status(http.StatusTooManyRequests).JSON(fiber.Map{
				"error": "Too many requests",
			})
		}

		return c.Next()
	})

	app.Get("/test", func(c *fiber.Ctx) error {
		return c.SendString("Hello, World!")
	})

	app.Listen(":8080")
}

