package main

import (
	"net/http"
	"sync"

	"github.com/gin-gonic/gin"
	"k8s.io/klog/v2"
)

type Options struct {
	ListenAddr string
}

type Counter struct {
	mu     sync.Mutex
	count  int
}

var counter = Counter{}

func (c *Counter) Increment() {
	c.mu.Lock()
	defer c.mu.Unlock()
	c.count++
}

func (c *Counter) Reset() {
	c.mu.Lock()
	defer c.mu.Unlock()
	c.count = 0
}

func (c *Counter) Get() int {
	c.mu.Lock()
	defer c.mu.Unlock()
	return c.count
}

func Run(op *Options) error {
	gin.SetMode(gin.ReleaseMode)
	r := gin.Default()

	r.GET("/api/v1/ping", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "pong"})
	})

	r.GET("/api/v1/info", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"info": "This is a simple HTTP server using Gin."})
	})

	r.POST("/api/v1/counter/increment", func(c *gin.Context) {
		counter.Increment()
		c.JSON(http.StatusOK, gin.H{"count": counter.Get()})
	})

	r.POST("/api/v1/counter/reset", func(c *gin.Context) {
		counter.Reset()
		c.JSON(http.StatusOK, gin.H{"count": counter.Get()})
	})

	klog.Infof("Starting server on %s", op.ListenAddr)
	return r.Run(op.ListenAddr)
}

