package main

import (
	"fmt"
	"math/rand"
	"net/http"
	"os"
	"path/filepath"
	"time"

	"github.com/gin-gonic/gin"
)

func main() {
	r := gin.Default()
	r.Static("/files", "./root/file")

	r.POST("/upload", func(c *gin.Context) {
		file, err := c.FormFile("file")
		if err != nil {
			c.JSON(http.StatusBadRequest, gin.H{
				"status": 1,
				"message": "File upload error",
			})
			return
		}

		ext := filepath.Ext(file.Filename)
		timestamp := time.Now().Unix()
		randomString := randString(8)
		newFileName := fmt.Sprintf("%d-%s%s", timestamp, randomString, ext)

		if err := c.SaveUploadedFile(file, filepath.Join("root/file", newFileName)); err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{
				"status": 2,
				"message": "File saving error",
			})
			return
		}

		fileURL := fmt.Sprintf("/files/%s", newFileName)
		c.JSON(http.StatusOK, gin.H{
			"status": 0,
			"message": "File uploaded successfully",
			"data": gin.H{
				"url": fileURL,
			},
		})
	})

	r.Run(":8085")
}

func randString(n int) string {
	const letters = "0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ"
	b := make([]byte, n)
	for i := range b {
		b[i] = letters[rand.Intn(len(letters))]
	}
	return string(b)
}

