func Tcp(ctx context.Context, req *pb.TcpRequest, rsp *pb.TcpResponse) error {
    conn, err := net.Dial("tcp", req.Address)
    if err != nil {
        rsp.Status = err.Error()
        return err
    }
    defer conn.Close()

    if req.Data != "" {
        _, err := conn.Write([]byte(req.Data))
        if err != nil {
            rsp.Status = err.Error()
            return err
        }
    }

    buffer := make([]byte, 1024)
    n, err := conn.Read(buffer)
    if err != nil && err.Error() != "EOF" {
        rsp.Status = err.Error()
        return err
    }

    rsp.Data = string(buffer[:n])
    rsp.Status = "OK"
    return nil
}

