import (
	"context"
	"crypto/rand"
	"encoding/binary"
	"net"
	"time"
)

type DNSEndpoint struct {
	Addr string
}

func (d *DNSEndpoint) Exchange(ctx context.Context, payload, buf []byte) (n int, err error) {
	conn, err := net.Dial("udp", d.Addr)
	if err != nil {
		return 0, err
	}
	defer conn.Close()

	if deadline, ok := ctx.Deadline(); ok {
		if err := conn.SetDeadline(deadline); err != nil {
			return 0, err
		}
	}

	txID := make([]byte, 2)
	if _, err := rand.Read(txID); err != nil {
		return 0, err
	}
	copy(payload[0:2], txID)

	if _, err := conn.Write(payload); err != nil {
		return 0, err
	}

	n, err = conn.Read(buf)
	if err != nil {
		return n, err
	}

	if !isValidResponse(buf, txID) {
		return n, err
	}

	return n, nil
}

func isValidResponse(buf, txID []byte) bool {
	return len(buf) >= 2 && buf[0] == txID[0] && buf[1] == txID[1]
}

