package main

import (
	"fmt"
	"net"
	"time"
)

type OpenTSDBConfig struct {
	Addr          *net.TCPAddr
	Registry      Registry
	FlushInterval time.Duration
	DurationUnit  time.Duration
	Prefix        string
}

type Registry interface {
	GetMetrics() []Metric
}

type Metric interface {
	Name() string
	Type() string
	Value() interface{}
}

type Client struct {
	config OpenTSDBConfig
}

func (c *Client) OpenTSDBWithConfig(config OpenTSDBConfig) {
	c.config = config
	ticker := time.NewTicker(c.config.FlushInterval)
	defer ticker.Stop()
	for {
		select {
		case <-ticker.C:
			if err := c.openTSDB(&c.config); err != nil {
				fmt.Println("Error sending metrics:", err)
			}
		}
	}
}

func (c *Client) openTSDB(config *OpenTSDBConfig) error {
	conn, err := net.DialTCP("tcp", nil, config.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	metrics := config.Registry.GetMetrics()
	for _, metric := range metrics {
		var line string
		switch metric.Type() {
		case "counter":
			line = fmt.Sprintf("%s.%s %v %d", config.Prefix, metric.Name(), metric.Value(), time.Now().Unix())
		case "gauge":
			line = fmt.Sprintf("%s.%s %v %d", config.Prefix, metric.Name(), metric.Value(), time.Now().Unix())
		case "histogram":
