		if err != nil {
			continue
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	defer conn.Close()
	reader := bufio.NewReader(conn)
	req, err := http.ReadRequest(reader)
	if err != nil {
		return
	}
	if h.user != "" && h.password != "" {
		auth := req.Header.Get("Authorization")
		if !h.checkAuth(auth) {
			conn.Write([]byte("HTTP/1.1 401 Unauthorized\r\nWWW-Authenticate: Basic realm=\"User Visible Realm\"\r\n\r\n"))
			return
		}
	}
	if req.Method == http.MethodConnect {
		h.servHTTPS(req, conn)
	} else {
		h.servHTTP(req, conn)
	}
}

func (h *HTTP) checkAuth(auth string) bool {
	if auth == "" {
		return false
	}
	payload := strings.SplitN(auth, " ", 2)
	if len(payload) != 2 || payload[0] != "Basic" {
		return false
	}
	decoded, err := base64.StdEncoding.DecodeString(payload[1])
	if err != nil {
		return false
	}
	credentials := strings.SplitN(string(decoded), ":", 2)
	return len(credentials) == 2 && credentials[0] == h.user && credentials[1] == h.password
}

func (h *HTTP) servHTTPS(req *http.Request, conn net.Conn) {
	host := req.URL.Host
	tunnel := fmt.Sprintf("HTTP/1.1 200 Connection Established\r\n\r\n")
	conn.Write([]byte(tunnel))
	// Establish a tunnel
	client := &http.Client{}
	// Handle the connection
}

func (h *HTTP) servHTTP(req *http.Request, conn net.Conn) {
	if h.pretend {
		conn.Write([]byte("HTTP/1.1 404 Not Found\r\n\r\n"))
		return
	}
	// Forward the request to the actual server
	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return
	}
	defer resp.Body.Close()
	// Write response back to client
	io.Copy(conn, resp.Body)
}

