	Timeout        time.Duration
	MaxConnections int
	MaxBytes       int
}

type unix struct {
	name      string
	config    *Config
	done      chan struct{}
	eventPool *event.Pool
}

func (u *unix) ProductLoop(productFunc api.ProductFunc) {
	listener, err := net.Listen("unix", u.config.Path)
	if err != nil {
		fmt.Println("Error starting server:", err)
		return
	}
	defer listener.Close()

	go func() {
		<-u.done
		listener.Close()
	}()

	for {
		conn, err := listener.Accept()
		if err != nil {
			select {
			case <-u.done:
				return
			default:
				fmt.Println("Error accepting connection:", err)
				continue
			}
		}
		go u.handleConn(context.Background(), conn, productFunc)
	}
}

func (u *unix) handleConn(ctx context.Context, conn net.Conn, productFunc api.ProductFunc) {
	defer conn.Close()
	ctx, cancel := context.WithTimeout(ctx, u.config.Timeout)
	defer cancel()

	buf := make([]byte, u.config.MaxBytes)
	n, err := conn.Read(buf)
	if err != nil {
		fmt.Println("Error reading from connection:", err)
		return
	}

	data := buf[:n]
	productFunc(data)
}

func (u *unix) checkBind(path string) error {
	if _, err := os.Stat(path); err == nil {
		if err := os.Remove(path); err != nil {
			return fmt.Errorf("failed to remove existing socket: %w", err)
		}
	}
	return nil
}

func (u *unix) chmod(path string, mode os.FileMode) error {
	return os.Chmod(path, mode)
}

