import (
	"log"
	"os"
	"os/signal"
	"syscall"

	"github.com/streadway/amqp"
)

type Consumer struct {
	conn   *amqp.Connection
	channel *amqp.Channel
	tag    string
	done   chan error
}

func NewConsumer(amqpURI string, exchange string, exchangeType string, queueName string, key string, ctag string) (*Consumer, error) {
	conn, err := amqp.Dial(amqpURI)
	if err != nil {
		return nil, err
	}

	channel, err := conn.Channel()
	if err != nil {
		return nil, err
	}

	if err := channel.ExchangeDeclare(exchange, exchangeType, true, false, false, false, nil); err != nil {
		return nil, err
	}

	_, err = channel.QueueDeclare(queueName, true, false, false, false, nil)
	if err != nil {
		return nil, err
	}

	if err := channel.QueueBind(queueName, key, exchange, false, nil); err != nil {
		return nil, err
	}

	deliveries, err := channel.Consume(queueName, ctag, false, false, false, false, nil)
	if err != nil {
		return nil, err
	}

	c := &Consumer{
		conn:   conn,
		channel: channel,
		tag:    ctag,
		done:   make(chan error),
	}

	go c.handle(deliveries, c.done)

	return c, nil
}

func (c *Consumer) Shutdown() error {
	if err := c.channel.Cancel(c.tag, false); err != nil {
		return err
	}
	if err := c.conn.Close(); err != nil {
		return err
	}
	return <-c.done
}

func (c *Consumer) handle(deliveries <-chan amqp.Delivery, done chan error) {
	for d := range deliveries {
		log.Printf("Received a message: %s", d.Body)
		d.Ack(false)
	}
	done <- nil
}

