	conn    *amqp.Connection
	channel *amqp.Channel
	ex      bool
	t       time.Time
}

func NewAMQPConn(ep Endpoint) (*AMQPConn, error) {
	conn, err := amqp.Dial(ep.URL)
	if err != nil {
		return nil, err
	}
	channel, err := conn.Channel()
	if err != nil {
		return nil, err
	}
	return &AMQPConn{ep: ep, conn: conn, channel: channel, ex: false, t: time.Now()}, nil
}

func (a *AMQPConn) Send(msg string) error {
	a.mu.Lock()
	defer a.mu.Unlock()

	if a.ex {
		return amqp.ErrClosed
	}

	if a.channel == nil {
		channel, err := a.conn.Channel()
		if err != nil {
			return err
		}
		a.channel = channel
		if err := a.channel.ExchangeDeclare(
			a.ep.Exchange,
			"direct",
			true,
			false,
			false,
			false,
			nil,
		); err != nil {
			return err
		}
		if _, err := a.channel.QueueDeclare(
			a.ep.Queue,
			true,
			false,
			false,
			false,
			nil,
		); err != nil {
			return err
		}
	}

	err := a.channel.Publish(
		a.ep.Exchange,
		a.ep.Queue,
		false,
		false,
		amqp.Publishing{
			ContentType: "text/plain",
			Body:        []byte(msg),
		},
	)
	if err != nil {
		return err
	}

	a.t = time.Now()
	return nil
}

