package main

import (
	"net/http"
	"sync"

	"github.com/gorilla/websocket"
)

type Link struct {
	Local   *websocket.Conn
	Remote  *websocket.Conn
}

type Shell struct {
	Name  string
	links map[string]*Link
	mu    sync.RWMutex
}

func (s *Shell) WS(conn *websocket.Conn, w http.ResponseWriter, r *http.Request) {
	// Upgrade connection
	upgrader := websocket.Upgrader{}
	localConn, err := upgrader.Upgrade(w, r, nil)
	if err != nil {
		return
	}
	defer localConn.Close()

	id := conn.RemoteAddr().String() // Example ID, can be more sophisticated
	s.mu.Lock()
	s.links[id] = &Link{Local: localConn}
	s.mu.Unlock()

	go s.localForward(id, localConn)
	go s.remoteForward(id, localConn)

	select {}
}

func (s *Shell) localForward(id string, local *websocket.Conn) {
	for {
		_, msg, err := local.ReadMessage()
		if err != nil {
			break
		}
		s.mu.RLock()
		if link, ok := s.links[id]; ok && link.Remote != nil {
			link.Remote.WriteMessage(websocket.TextMessage, msg)
		}
		s.mu.RUnlock()
	}
	s.cleanup(id)
}

func (s *Shell) remoteForward(id string, local *websocket.Conn) {
	s.mu.RLock()
	link, ok := s.links[id]
	s.mu.RUnlock()
	if !ok {
		return
	}

	for {
		_, msg, err := link.Remote.ReadMessage()
		if err != nil {
			break
		}
		local.WriteMessage(websocket.TextMessage, msg)
	}
	s.cleanup(id)
}

func (s *Shell) cleanup(id string) {
	s.mu.Lock()
	defer s.mu.Unlock()
	if link, ok := s.links[id]; ok {
		if link.Local != nil {
			link.Local.Close()
		}
		if link.Remote != nil {
			link.Remote.Close()
		}
		delete(s.links, id)
	}
}

