package main

import (
	"fmt"
	"net"
	"time"
)

type network struct {
	gateway net.IP
}

func (n *network) call(msg []byte, timeout time.Duration) ([]byte, error) {
	addr := net.UDPAddr{
		IP:   n.gateway,
		Port: 5351,
	}
	conn, err := net.DialUDP("udp", nil, &addr)
	if err != nil {
		return nil, err
	}
	defer conn.Close()

	var response []byte
	var backoff time.Duration = 100 * time.Millisecond
	for attempts := 0; attempts < 5; attempts++ {
		if _, err := conn.SetDeadline(time.Now().Add(timeout)); err != nil {
			return nil, err
		}
		if _, err := conn.Write(msg); err != nil {
			return nil, err
		}

		response = make([]byte, 512)
		n, _, err := conn.ReadFromUDP(response)
		if err == nil {
			return response[:n], nil
		}

		time.Sleep(backoff)
		backoff *= 2
	}
	return nil, fmt.Errorf("failed to communicate with gateway after retries: %v", err)
}

