package main

import (
	"errors"
	"os"
	"os/exec"
	"time"
)

type proxyCommand struct {
	cmd        *exec.Cmd
	ExtraFiles []*os.File
}

func (p *proxyCommand) Start() error {
	pipeR, pipeW, err := os.Pipe()
	if err != nil {
		return err
	}
	defer pipeR.Close()
	defer pipeW.Close()

	p.ExtraFiles = append(p.ExtraFiles, pipeW)
	p.cmd.ExtraFiles = p.ExtraFiles

	if err := p.cmd.Start(); err != nil {
		return err
	}

	// Close the writer in the parent process
	pipeW.Close()

	// Set a timeout for the initialization
	timeout := time.After(5 * time.Second)
	status := make([]byte, 1)

	select {
	case <-timeout:
		p.cmd.Process.Kill()
		return errors.New("proxy process initialization timed out")
	default:
		n, err := pipeR.Read(status)
		if err != nil {
			p.cmd.Process.Kill()
			return err
		}
		if n == 0 || status[0] != 1 {
			p.cmd.Process.Kill()
			return errors.New("proxy process failed to initialize")
		}
	}

	return nil
}

