	"github.com/yourusername/scorecard" // replace with actual scorecard import path
	"log"
)

type options struct {
	LogLevel   string
	CommitDepth int
}

func serveCmd(o *options) *cobra.Command {
	cmd := &cobra.Command{
		Use:   "serve",
		Short: "Start the HTTP server for scorecard results",
		Run: func(cmd *cobra.Command, args []string) {
			http.HandleFunc("/scorecard", func(w http.ResponseWriter, r *http.Request) {
				repoURL := r.URL.Query().Get("repo")
				if repoURL == "" {
					http.Error(w, "Missing repo parameter", http.StatusBadRequest)
					return
				}

				results, err := scorecard.Run(repoURL, o.CommitDepth)
				if err != nil {
					http.Error(w, err.Error(), http.StatusInternalServerError)
					return
				}

				if r.Header.Get("Content-Type") == "application/json" {
					w.Header().Set("Content-Type", "application/json")
					json.NewEncoder(w).Encode(results)
				} else {
					w.Header().Set("Content-Type", "text/html")
					tmpl, err := template.New("scorecard").Parse(`
						<!DOCTYPE html>
						<html>
						<head><title>Scorecard Results</title></head>
						<body>
							<h1>Scorecard Results for {{.Repo}}</h1>
							<pre>{{.Results}}</pre>
						</body>
						</html>
					`)
					if err != nil {
						log.Fatalf("Error parsing template: %v", err)
					}
					if err := tmpl.Execute(w, map[string]interface{}{
						"Repo":    repoURL,
						"Results": results,
					}); err != nil {
						log.Fatalf("Error executing template: %v", err)
					}
				}
			})

			log.Printf("Starting server on :8080")
			if err := http.ListenAndServe(":8080", nil); err != nil {
				log.Fatalf("Error starting server: %v", err)
			}
		},
	}
	return cmd
}

