			if token != authToken {
				http.Error(w, "Forbidden", http.StatusForbidden)
				return
			}
		}

		switch r.Method {
		case http.MethodGet:
			// Handle GET requests
		case http.MethodPost:
			// Handle POST requests
		}
	})

	address := bind
	if address == "" {
		address = "0.0.0.0"
	}

	if tlsCrt != "" && tlsKey != "" {
		cert, err := tls.LoadX509KeyPair(tlsCrt, tlsKey)
		if err != nil {
			log.Fatalf("Failed to load TLS certificate: %v", err)
		}
		srv := &http.Server{
