package main

import (
	"net/http"
	"github.com/studio-b12/gowebdav"
	"log"
)

type webdavHandler struct {
	FileSystem webdav.FileSystem
	LockSystem webdav.LockSystem
	Logger     func(*http.Request, error)
}

func (h *webdavHandler) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	h.Logger(r, nil)
	h.FileSystem.ServeHTTP(w, r)
}

func NewMemFS() webdav.FileSystem {
	return gowebdav.NewMemFS()
}

func NewMemLS() webdav.LockSystem {
	return gowebdav.NewMemLS()
}

func main() {
	fs := NewMemFS()
	ls := NewMemLS()
	handler := &webdavHandler{
		FileSystem: fs,
		LockSystem: ls,
		Logger: func(r *http.Request, err error) {
			if err != nil {
				log.Printf("Error: %v, Method: %s, URL: %s", err, r.Method, r.URL)
			} else {
				log.Printf("Request: Method: %s, URL: %s", r.Method, r.URL)
			}
		},
	}

	http.HandleFunc("/", requestHandler(handler.ServeHTTP))
	log.Fatal(http.ListenAndServe(":8080", nil))
}

func requestHandler(next http.HandlerFunc) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		if r.Header.Get("X-Custom-Header") != "" {
			w.Header().Set("X-Processed-Header", "Processed")
		}
		next(w, r)
	}
}

