package main

import (
	"github.com/labstack/echo/v4"
	"github.com/labstack/echo/v4/middleware"
	"github.com/labstack/gommon/log"
	"io/ioutil"
	"net/http"
)

type mjHandler struct {
	log                   echo.Logger
	tenhouMessageReceiver *tenhou.MessageReceiver
	majsoulMessageQueue   chan []byte
}

func (h *mjHandler) analysisTenhou(c echo.Context) error {
	body, err := ioutil.ReadAll(c.Request().Body)
	if err != nil {
		h.log.Errorf("Failed to read request body for /tenhou: %v", err)
		return c.JSON(http.StatusInternalServerError, map[string]string{"error": "Failed to read request body"})
	}
	defer c.Request().Body.Close()

	err = h.tenhouMessageReceiver.Receive(body)
	if err != nil {
		h.log.Errorf("Failed to forward message to Tenhou receiver: %v", err)
		return c.JSON(http.StatusInternalServerError, map[string]string{"error": "Failed to forward message"})
	}

	return c.JSON(http.StatusOK, map[string]string{"message": "Message forwarded to Tenhou"})
}

func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	body, err := ioutil.ReadAll(c.Request().Body)
	if err != nil {
		h.log.Errorf("Failed to read request body for /majsoul: %v", err)
		return c.JSON(http.StatusInternalServerError, map[string]string{"error": "Failed to read request body"})
	}
	defer c.Request().Body.Close()

	h.majsoulMessageQueue <- body

	return c.JSON(http.StatusOK, map[string]string{"message": "Message queued for Majsoul"})
}

func runServer(isHTTPS bool, port int) error {
	e := echo.New()
	e.Use(middleware.Logger())
	e.Use(middleware.Recover())

	mjHandler := &mjHandler{
		log:                   e.Logger,
		tenhouMessageReceiver: &tenhou.MessageReceiver{},
		majsoulMessageQueue:   make(chan []byte),
	}

	e.POST("/tenhou", mjHandler.analysisTenhou)
	e.POST("/majsoul", mjHandler.analysisMajsoul)

	server := &http.Server{
		Addr:    fmt.Sprintf(":%d", port),
		Handler: e,
	}

	if isHTTPS {
		return server.ListenAndServeTLS("cert.pem", "key.pem")
	}

	return server.ListenAndServe()
}
