func extractResource(c *gin.Context) string {
	ip := c.ClientIP()
	if ip == "" {
		ip = "unknown"
	}
	return ip
}

func blockFallbackHandler(c *gin.Context, err *base.BlockError) {
	c.JSON(http.StatusTooManyRequests, gin.H{
		"error": "Too Many Requests",
		"message": err.Error(),
	})
