						return
					}
				}
				i, err := tc.Read(bf[:])
				if err != nil {
					return
				}
				p := &zoro.TCPPacket{
					Address: tc.RemoteAddr().String(),
					Data:    bf[0:i],
				}
				b, err := proto.Marshal(p)
				if err != nil {
					select {
					case <-s.Done:
						return
					case s.Error <- err:
					}
					return
				}
				bb := make([]byte, 2)
				binary.BigEndian.PutUint16(bb, uint16(len(b)))
				d := DomainData{
					Domain: strings.TrimSuffix(cs.ServerName, "."+s.HTTPSServer.Domain),
					Data:   append(append([]byte{0x01}, bb...), b...),
				}
				select {
				case <-s.Done:
					return
				case s.DomainData <- d:
				}
			}
		}(tc)
