	return t, nil
}

func (t *HTTPTransport) Append(span *jaeger.Span) (int, error) {
	t.mu.Lock()
	defer t.mu.Unlock()

	zipkinSpan, err := span.ToZipkinSpan(context.Background())
	if err != nil {
		return 0, err
	}

	t.batch = append(t.batch, zipkinSpan)
	if len(t.batch) >= t.batchSize {
		return t.Flush()
	}
	return len(t.batch), nil
}

func (t *HTTPTransport) Flush() (int, error) {
	t.mu.Lock()
	defer t.mu.Unlock()

	if len(t.batch) == 0 {
		return 0, nil
	}

	err := t.send(t.batch)
	if err != nil {
		return 0, err
	}

	t.batch = nil
	return 0, nil
}

func (t *HTTPTransport) Close() error {
	return nil
}

func (t *HTTPTransport) send(spans []*zipkincore.Span) error {
	body, err := t.httpSerialize(spans)
	if err != nil {
		return err
	}

	req, err := http.NewRequest("POST", t.url, body)
	if err != nil {
		return err
	}
	req.Header.Set("Content-Type", "application/x-thrift")

	if t.httpCredentials != nil {
		req.SetBasicAuth(t.httpCredentials.username, t.httpCredentials.password)
	}

	resp, err := t.client.Do(req)
	if err != nil {
		return err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		return fmt.Errorf("unexpected status code: %d", resp.StatusCode)
	}

	return nil
}

func (t *HTTPTransport) httpSerialize(spans []*zipkincore.Span) (*bytes.Buffer, error) {
	buf := new(bytes.Buffer)
	protocolFactory := thrift.NewTBinaryProtocolFactoryDefault()
	transport := thrift.NewTBufferedTransport(buf, 1024)
	protocol := protocolFactory.GetProtocol(transport)

	for _, span := range spans {
		if err := span.Write(protocol); err != nil {
			return nil, err
		}
	}

	if err := transport.Flush(); err != nil {
		return nil, err
	}

	return buf, nil
}

type HTTPOption func(*HTTPTransport) error

func WithLogger(logger jaeger.Logger) HTTPOption {
	return func(t *HTTPTransport) error {
		t.logger = logger
		return nil
	}
}

func WithBatchSize(size int) HTTPOption {
	return func(t *HTTPTransport) error {
		t.batchSize = size
		return nil
	}
}

func WithHTTPClient(client *http.Client) HTTPOption {
	return func(t *HTTPTransport) error {
		t.client = client
		return nil
	}
}

func WithHTTPCredentials(username, password string) HTTPOption {
	return func(t *HTTPTransport) error {
		t.httpCredentials = &HTTPBasicAuthCredentials{
			username: username,
			password: password,
		}
		return nil
	}
}
