var (
	redisMap map[string]*RedisClient
	mapMutex *sync.RWMutex
)

const (
	defaultMaxIdle   = 10
	defaultMaxActive = 50
)

func init() {
	redisMap = make(map[string]*RedisClient)
	mapMutex = new(sync.RWMutex)
}

func newPool(redisURL string, maxIdle, maxActive int) *redis.Pool {
	return &redis.Pool{
		MaxIdle:   maxIdle,
		MaxActive: maxActive,
		Dial: func() (redis.Conn, error) {
			c, err := redis.DialURL(redisURL)
			return c, err
		},
	}
}

func GetRedisClient(address string, maxIdle, maxActive int) *RedisClient {
	if maxIdle <= 0 {
		maxIdle = defaultMaxIdle
	}
	if maxActive <= 0 {
		maxActive = defaultMaxActive
	}
	var redis *RedisClient
	var mok bool
	mapMutex.RLock()
	redis, mok = redisMap[address]
	mapMutex.RUnlock()
	if !mok {
		redis = &RedisClient{Address: address, pool: newPool(address, maxIdle, maxActive)}
		mapMutex.Lock()
		redisMap[address] = redis
		mapMutex.Unlock()
	}
	return redis
}

func (rc *RedisClient) Get(key string) (string, error) {
	conn := rc.pool.Get()
	defer conn.Close()
	reply, errDo := conn.Do("GET", key)
