	GroupName  string
}

type Producer struct {
	started    bool
	Producer   rocketmq.Producer
	name       string
	ProducerConfig
	interceptors []primitive.Interceptor
	mu         sync.Mutex
}

func NewProducer(name string, config ProducerConfig) *Producer {
	return &Producer{
		name:           name,
		ProducerConfig: config,
	}
}

func (p *Producer) Start() error {
	p.mu.Lock()
	defer p.mu.Unlock()

	if p.started {
		return errors.New("producer already started")
	}

	var err error
	p.Producer, err = rocketmq.NewProducer(
		rocketmq.WithNameServer([]string{p.NameServer}),
		rocketmq.WithGroupName(p.GroupName),
	)
	if err != nil {
		return err
	}

	err = p.Producer.Start()
	if err != nil {
		return err
	}

	p.started = true
	return nil
}

func (p *Producer) Close() error {
	p.mu.Lock()
	defer p.mu.Unlock()

	if !p.started {
		return errors.New("producer not started")
	}

	err := p.Producer.Shutdown()
	if err != nil {
		return err
	}

	p.started = false
	return nil
}

func (p *Producer) SendWithContext(ctx context.Context, msg []byte) error {
	if !p.started {
		return errors.New("producer not started")
	}

	rm := &primitive.Message{
		Topic: "default",
		Body:  msg,
	}

	for _, interceptor := range p.interceptors {
		rm = interceptor(ctx, rm)
	}

	_, err := p.Producer.SendMessageInTransaction(ctx, rm, nil)
	return err
}

func (p *Producer) SendWithMsg(ctx context.Context, msg *primitive.Message) error {
	if !p.started {
		return errors.New("producer not started")
	}

	for _, interceptor := range p.interceptors {
		msg = interceptor(ctx, msg)
	}

	_, err := p.Producer.SendMessageInTransaction(ctx, msg, nil)
	return err
}

func (p *Producer) SendWithResult(ctx context.Context, msg []byte, tag string) (*primitive.SendResult, error) {
	if !p.started {
		return nil, errors.New("producer not started")
	}

	rm := &primitive.Message{
		Topic: "default",
		Body:  msg,
		Tags:  tag,
	}

	for _, interceptor := range p.interceptors {
		rm = interceptor(ctx, rm)
	}

	result, err := p.Producer.SendMessage(ctx, rm)
	return result, err
}

func (p *Producer) SendMsg(ctx context.Context, msg *primitive.Message) (*primitive.SendResult, error) {
	if !p.started {
		return nil, errors.New("producer not started")
	}

	for _, interceptor := range p.interceptors {
		msg = interceptor(ctx, msg)
	}

	result, err := p.Producer.SendMessage(ctx, msg)
	return result, err
}
