type UploadSession struct {
	ID                string
	OldID             string
	ParentID          string
	NodeID            uint64
	Name              string
	ExpirationDateTime time.Time
	Size              uint64
	Data              []byte
	QuickXORHash      string
	ModTime           time.Time
	retries           int
	UploadURL         string
	ETag              string
	state             int
	error             error
	sync.Mutex
}

func (us *UploadSession) uploadChunk(auth *graph.Auth, offset uint64) ([]byte, int, error) {
	us.Lock()
	defer us.Unlock()

	chunkSize := 1024 * 1024 // Example chunk size of 1MB
	end := offset + uint64(chunkSize)
	if end > us.Size {
		end = us.Size
	}
	chunk := us.Data[offset:end]

	req, err := http.NewRequest("PUT", us.UploadURL, bytes.NewReader(chunk))
	if err != nil {
		return nil, 0, err
	}

	req.Header.Set("Content-Type", "application/octet-stream")
	req.Header.Set("Content-Range", fmt.Sprintf("bytes %d-%d/%d", offset, end-1, us.Size))
	req.Header.Set("Authorization", "Bearer "+auth.Token)

	client := &http.Client{}
	resp, err := client.Do(req)
	if err != nil {
		return nil, 0, err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, 0, err
	}

	if resp.StatusCode == http.StatusUnauthorized {
		if err := auth.Refresh(); err != nil {
			return nil, 0, err
		}
		return us.uploadChunk(auth, offset)
	}

	us.retries = 0
	us.ETag = resp.Header.Get("ETag")

	return body, resp.StatusCode, nil
}
