	if err != nil {
		for _, dp := range dpList {
			resQueue <- &push.Result{DeliveryPoint: dp, Error: err}
		}
		return
	}
	defer resp.Body.Close()

	var results []CMResult
	if err := json.NewDecoder(resp.Body).Decode(&results); err != nil {
		for _, dp := range dpList {
			resQueue <- &push.Result{DeliveryPoint: dp, Error: err}
		}
		return
	}

	psb.handleCMMulticastResults(psp, dpList, resQueue, notif, results)
