	v, err := config.Get("micro.gifs")
	if err != nil {
		logger.Fatalf("Failed to load config %s", err)
	}
	var c conf
	if err := v.Scan(&c); err != nil {
		logger.Fatalf("Failed to load config %s", err)
	}

	return &Gifs{conf: c}
